  }
  function amountChangeHandler(event){
    setEnteredAmount(event.target.value);
  }
  function dateChangeHandler(event){
    setEnteredDate(event.target.value);
  }
  return (
    <form>
      <div className="new-expense_controls">
        <div className="new-expense__control">
          <label>Title</label> <input type="text" onChange={titleChangeHandler}/>
        </div>
        <div className="new-expense__control">
          <label>Amount</label> <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler} />
        </div>
        <div className="new-expense__control">
          <label>Date</label> <input type="date" min = "2022-7-24" max="2999-12-31" onChange={dateChangeHandler}/>
        </div>
        
      </div>
      <div className="new-expense__actions">
        <button type = "submit">Add Expense</button>
      </div>
    </form>
  );
}
export default ExpenseForm;
